import DOMComponent from "nss-domcomponent"

/* Define an <h1> element that you can create whenever you want one */
class h1 extends DOMComponent {
    constructor(attributes, ...children) {
        super("h1", attributes, ...children)
        return this
    }
}

/* Define a <section> element that you can create whenever you want one */
class section extends DOMComponent {
    constructor(attributes, ...children) {
        super("section", attributes, ...children)
        return this
    }
}

/* Define a <p> element that you can create whenever you want one */
class p extends DOMComponent {
    constructor(attributes, ...children) {
        super("p", attributes, ...children)
        return this
    }
}

/* Time to create an <h1> element */
const title = new h1({
    className: "mainHeader",
    textContent: "Navigation"
})

/*
    Create a section element, with two children elements
        - The <h1> you created above
        - A new <p> created inline
 */
const body = new section(
    {
        className: "mainSection",
        textContent: "Main body"
    },
    title,
    new p({
        className: "intro",
        textContent: "Intro paragraph"
    })
).render(".output")


















