class BlogArticle extends DOMComponent {
    constructor() {
        const structure = new section(
            {
                className: "section"
            },
            new h1({
                className: "section__header",
                textContent: "Blog Title"
            }),
            new p({
                className: "section__body",
                textContent: "Article content"
            })
        )
        super("article", {}, structure)
    }
}



